// src/components/Navbar.js
import React, { useEffect, useState } from "react";
import { useRef } from "react";
import "./Navbar.css"; // Import the CSS file for styles
import Lenis from "@studio-freight/lenis";

const Navbar = () => {
  const [isVisible, setIsVisible] = useState(true); // State to control visibility
  const lenis = useRef(null); // Reference to Lenis instance

  useEffect(() => {
    // Initialize Lenis
    lenis.current = new Lenis({
      duration: 1.2, // Duration of the scroll animation
      easing: (t) => t, // Easing function
      smooth: true, // Enable smooth scrolling
    });

    // Scroll animation
    function raf(time) {
      lenis.current.raf(time);
      requestAnimationFrame(raf);
    }

    requestAnimationFrame(raf);

    let lastScrollTop = 0;
    const handleScroll = () => {
      let scrollTop = window.pageYOffset || document.documentElement.scrollTop;

      if (scrollTop > lastScrollTop) {
        // Scrolling down
        setIsVisible(false); // Hide the navbar
      } else {
        // Scrolling up
        setIsVisible(true); // Show the navbar
      }

      lastScrollTop = scrollTop;
    };

    window.addEventListener("scroll", handleScroll);

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // Handle smooth scrolling
  const handleSmoothScroll = (e, targetId) => {
    e.preventDefault(); // Prevent default anchor link behavior
    const targetElement = document.getElementById(targetId);
    if (targetElement) {
      lenis.current.scrollTo(targetElement.offsetTop); // Use Lenis to scroll to the target element
    }
  };

  return (
    <nav className={`navbar navbar-expand-lg sticky-top shadow-sm ${isVisible ? "visible" : "hidden"}`} style={{ zIndex: 999 }}>
      <div className="container-fluid">
        <a className="navbar-brand text-light fw-bolder ms-3" href="#">
          Portofolio
        </a>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <a className="text-light fw-bolder nav-link active" aria-current="page" href="#home" onClick={(e) => handleSmoothScroll(e, "home")}>
                Home
              </a>
            </li>
            <li className="nav-item">
              <a className="text-light nav-link" href="#about" onClick={(e) => handleSmoothScroll(e, "about")}>
                About Me
              </a>
            </li>
            <li className="nav-item">
              <a className="text-light nav-link" href="#project" onClick={(e) => handleSmoothScroll(e, "project")}>
                Project
              </a>
            </li>
            <li className="nav-item">
              <a className="text-light nav-link" href="#contact" onClick={(e) => handleSmoothScroll(e, "contact")}>
                Contact Me
              </a>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
