// src/components/Contact.js
import React, { useState } from "react";

const Contact = () => {
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    const form = e.target;
    const scriptURL = "https://script.google.com/macros/s/AKfycbydSYCrqpn9u80hRF16pKn_P8UAxBFSoeVA-HOid-ibAraK9icrVbRv3h_SMpNmcOd9/exec";

    fetch(scriptURL, { method: "POST", body: new FormData(form) })
      .then((response) => {
        console.log("Success!", response);
        setLoading(false);
        setSuccess(true);
        form.reset();
      })
      .catch((error) => {
        console.error("Error!", error.message);
        setLoading(false);
      });
  };

  return (
    <section className="contact-color text-light" id="contact">
      <div className="container">
        <div className="row text-center mb-3">
          <div className="col">
            <h2>Contact Me</h2>
          </div>
        </div>
        <div className="row justify-content-center">
          <div className="col-md-6">
            {success && (
              <div className="alert alert-success alert-dismissible fade show" role="alert">
                <strong>Thank You!!</strong> Your message has been sent.
                <button type="button" className="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
              </div>
            )}
            <form name="contact-form" onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="name" className="form-label">
                  Full Name
                </label>
                <input name="name" type="text" className="form-control" id="name" required />
              </div>
              <div className="mb-3">
                <label htmlFor="email" className="form-label">
                  Email
                </label>
                <input name="email" type="email" className="form-control" id="email" required />
              </div>
              <div className="mb-3">
                <label htmlFor="Message" className="form-label">
                  Message
                </label>
                <textarea name="message" className="form-control" id="Message" rows="3" required></textarea>
              </div>
              <button type="submit" className="btn btn-secondary mb-4 btn-send" disabled={loading}>
                {loading ? "Submitting..." : "Submit"}
              </button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
