// src/components/Navbar.js
import React, { useEffect } from "react";
import { gsap } from "gsap"; // Import GSAP

const Navbar = () => {
  useEffect(() => {
    let lastScrollTop = 0;
    const navbar = document.querySelector(".navbar");

    window.addEventListener("scroll", function () {
      let scrollTop = window.pageYOffset || document.documentElement.scrollTop;

      if (scrollTop > lastScrollTop) {
        // Scrolling down
        gsap.to(navbar, { top: "-80px", duration: 1, ease: "power2.out" }); // Increased duration to 1 second
      } else {
        // Scrolling up
        gsap.to(navbar, { top: "0", duration: 1, ease: "power2.out" }); // Increased duration to 1 second
      }

      lastScrollTop = scrollTop;
    });

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener("scroll", () => {});
    };
  }, []);

  return (
    <nav className="navbar navbar-expand-lg sticky-top shadow-sm" style={{ zIndex: 999 }}>
      <div className="container-fluid">
        <a className="navbar-brand text-light fw-bolder ms-3" href="#">
          Portofolio
        </a>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <a className="text-light fw-bolder nav-link active" aria-current="page" href="#home">
                Home
              </a>
            </li>
            <li className="nav-item">
              <a className="text-light nav-link" href="#about">
                About Me
              </a>
            </li>
            <li className="nav-item">
              <a className="text-light nav-link" href="#project">
                Project
              </a>
            </li>
            <li className="nav-item">
              <a className="text-light nav-link" href="#contact">
                Contact Me
              </a>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
